INSERT INTO recipes (id, name, description, ingredients, instructions, category_id, created_at, updated_at) VALUES (1, 'Pastitsio', NULL, '3 cups Penne Pasta<br>1/4 cup Soy butter/margarine, coarsely chopped<br>16 oz can Whole canned tomoatoes with juice<br>8-10 cloves Pressed garlic<br>3 tbsp Olive oil (Virgin/Extra Virgin preferred)<br>2 tbsp Fresh basil or 1 Tsp dried basil<br>1/2 tsp Salt<br>1 1/2 cups Soy milk<br>4 tbsp Whole wheat flour<br>1/3 cup Soy cream Cheese (Tofuitti Brand)<br>A pinch Salt/Pepper to taste', 'Preheat oven to 400 degrees.  Boil the Penne Pasta (add 2 Tsp Salt for taste).  Cook until almost al dente (almost cooked, a bit undercooked)<br>Drain pasta thoroughly and mix with butter in a separate bowl.<br>Blenderize tomatoes, garlic, olive oil, basil, and salt until smooth.<br>Heat 1/2 of milk gently in saucepan.<br>Blenderize the other half with the flour and add it when the milk in the sauce pan is warm.<br>Add Soy Cream Cheese to sauce .<br>Whisk the sauce, regularly, while it heats, and keep a sharp eye on it so it doesn''t burn or boil.  When it begins to boil, reduce the heat to a simmer and cook for five minutes.  Season to taste with salt & pepper.<br>Butter a 2 1/2 inch deep a baking dish (preferably a casserole dish).<br>Layer  1/2 the creamy sauce, 1/2 the pasta, 1/2 the tomato sauce.  Repeat layers until all sauces, pasta are gone.<br>Bake uncovered for 35 minutes at 400 degrees', 105, '2001-01-01 00:00:00', NULL);
INSERT INTO recipes (2, 'Easy Pasta Salad', 'Easy Pasta Salad - Quick meal', '1 lb. pasta (cooked and drained)<br>8 oz. frozen lima beans (cooked and drained)<br>3 carrots (finely chopped)<br>1 tsp dill weed<br>1/2 tsp salt (optional)<br>4 tbsp veganaise (vegan mayonaise)', 'Mix all ingredients together in a bowl. If time permits, chill before serving.', 110, NULL, NULL);
INSERT INTO recipes (3, 'Sweet Pea Salad', NULL, '(2) 15 oz cans Organic sweet peas<br>3/4 cup Slivered almonds<br>3/4 cup Sliced olives<br>12 oz French dressing', 'Mix all ingredients together in a bowl. Server chilled.', 110, NULL, NULL);
INSERT INTO recipes (4, 'Speedy Gonzales Bean and Cucumber Salad', NULL, '2 cucumbers (sliced)<br>1 can Kidney beans<br>1 can Diced Celery<br>2 tbsp mild Pace Picante sauce<br>4 tbsp Veganaise  (Soy/Vegan Mayonasise)', 'Mix all ingredients together in a bowl. If time permits, chill before serving.', 110, NULL, NULL);
INSERT INTO recipes (5, '3 Bean Salad', '', '1 tbsp Apple cider vinegar<br>1 tsp Mustard seed<br>some Salt/ pepper to taste<br>1 cup Yellow beans<br>1 cup Green beans<br>1 cup Chickpeas (Garbonzo bean)<br>1 medium Onion (diced or chopped)<br>some Pimiento<br>pinch Sugar', 'Mix all ingredients together in a bowl. If time permits, chill before serving.', 110, NULL, NULL);
INSERT INTO recipes (6, 'Lentil Confetti Salad', NULL, '1 cup Lentils<br>1 cup Brown rice<br>1 tsp Salt<br>7 cups Boiling water<br>1 medium Chopped red onion<br>1 small Green pepper<br>1 tbsp Italian dressing<br>some Olives, celery, mushrooms, or cherry tomatoes', 'Add lentils, brown rice, and salt to the boiling water.<br>Cook covered for 40 minutes.<br>Drain cooked lentils and rice.  Then add onion, green pepper, dressing, and any other favorite vegetables.<br>Make this at least 12 hours before serving.  Serve chilled.', 110, NULL, NULL);
INSERT INTO recipes (7, 'Parsleyed Red Potatoes', NULL, '5 lb Red potatoes(organic), diced<br>2 cubes "Rapunzel" vegan bullion<br>3 tbsp Margarine (non-dairy non-hydrog)<br>1/2 bunch Chopped parsley<br>some Salt/pepper to taste', 'Boil potatoes in water with bullion until almost tender.<br>Drain.  Add margarine and parsley.  Serve hot.', 113, NULL, NULL);
INSERT INTO recipes (8, 'Asian Cucumber and White Radish Salad', 'NOTE - You can substitute other firm veggies like carrots, kohlragi, sweet peppers.', '6-8 med Cucumbers (peeled and sliced 1/8 inch thick)<br>3-4 cups White or rice vinegar<br>1 cup White sugar<br>1 tbsp Salt<br>1/2 - 1 tbsp Dried pepper flakes<br>1 - 2 tbsp Sesame oil<br>1 bunch or 1/2 lb Radishes (white or Daikon) scrubbed and thinly sliced', 'In microwave, warm the dressing ingredients enough to dissolve sugar and let flavors blend.  Do not boil.<br>Pour warm dressing over veggies and toss.<br>Chill at least 2 hours or overnight.', 110, NULL, NULL);
INSERT INTO recipes (9, 'Avacado Dip', NULL, '2 medium Avocados (mashed)<br>1 tsp fresh Garlic (pressed)<br>2 tsp fresh lemon juice<br>1 med tomato (very finely diced)<br>1/4 cup mild salsa<br>1 tsp salt/pepper to taste', 'Mix all ingredients together.  Chill before serving.', 114, NULL, NULL);
INSERT INTO recipes (10, 'Split Pea Herb Soup', NULL, '4 1/2 cups Water<br>2 oz Vegetable base<br>1/2 cup Chopped onion<br>3 tbsp Lemon juice<br>1 1/2 tsp Sugar<br>1/2 tsp Salt<br>1/4 tsp Nutmeg<br>1/4 tsp Cayenne<br>1/4 tsp Marjoram<br>1/4 tsp Thyme<br>1/4 tsp Pepper<br>5 oz Chopped spinach<br>1 cup Grated carrots<br>1 tbsp Parsley flakes<br>1 lb Yellow split peas', 'Mix peas, water, vegetable base, onion, lemon juice, sugar, salt, nutmeg, cayenne, marjoram, thyme, and pepper in a large saucepan. Cook for 45-60 minutes.<br>Whisk until peas are pureed.  Then add spinach, carrots, and parlsey.  Heat through before serving for approximately 10 minutes.', 119, NULL, NULL);
INSERT INTO recipes (11, 'Vegan Peanut Better Bars', NULL, '8 cups Organic raisin bran flakes<br>2 cups Llight corn syrup<br>2 cups Organic sugar<br>2 1/2 cups Organic peanut butter<br>3 cups Vegan chocolate chips', 'Lightly oil 8 x 12 inch cake pan, and place the bran flakes in the pan.<br>Mix the corn syrup and sugar in a saucepan, and bring to a light boil.<br>Remove from heat, add the peanut butter, and mix until smooth.<br>Pour the mixture over the flakes, and combine.<br>Frosting: Melt the chocolate chips (in a double boiler or in the microwave) and pour over the top.  Place in the refrigerator overnight, and cut and serve the next day.', 108, NULL, NULL);
INSERT INTO recipes (12, 'Tahini Spread', NULL, '1 Large carrot<br>1 Celery stalk<br>2 tbsp Onion<br>1 tbsp Green onion<br>1/2 cup Fresh parsley<br>1/4 cup Fresh dill (2 tbsp dried dill)<br>1 cup Thick tahini<br>1/3 cup Lemon juice<br>2 tbsp Bragg''s liquid aminos<br>2 tsp Kelp powder', 'Finely chop and blend carrot, celery, onion, green onion, parsley, and dill.<br>Add tahini, lemon juice, liquid aminos, and kelp powder.<br>Mix well and serve. (Chill if desired).', 114, NULL, NULL);
INSERT INTO recipes (13, 'Artichoke Rice Salad', NULL, '1 cup Uncooked brown rice<br>1/4 cup Green onion<br>1/4 cup Chopped fresh parsley<br>1 Chopped small red bell pepper<br>14 oz can Artichokes, drained and quartered<br>1/4 cup Olive oil<br>1 tbsp Grated lemon peel<br>3 tbsp Lemon juice<br>1/2 tsp Salt<br>1/4 tsp Pepper<br>1 clove Minced garlic', 'Cook the rice<br>Make the Lemon Garlic Vinagrette - Mix the olive oil, lemon peel, salt, pepper, and garlic.<br>Mix everything together and refrigerate for at least 3 hours.  Serve chilled!', 110, NULL, NULL);
INSERT INTO recipes (14, 'Chop Suey Stir-fry', NULL, '1/2 cup Bamboo sprouts<br>1/2 cup Bean sprouts<br>1/2 cup Water chestnuts<br>1/3 cup Mushrooms<br>1 cup Mock duck or Mock chicken<br>1 1/2 cup Water<br>1/2 tsp Salt<br>2 tbsp Soy sauce<br>1 tsp Sugar<br>1/2 tsp Cornstarch<br>1/2 cup Water', 'Make the Peanut Oil Gravy - Mix water, salt, sugar, soy sauce, cornstarch, Chinese molasses, and water.<br>Stir-Fry the vegetables.  Add gravy before adding vegetables. Cook and Serve with rice.', 103, NULL, NULL);
INSERT INTO recipes (15, 'Best Carob Fudge', NULL, '2 cup Sunspire carob chips<br>1 cup All-natural peanut butter<br>1 cup Raisins<br>1 cup Thinly sliced almonds, or coarsley chopped pecans<br>1 cup Shredded, unsweetened coconut', 'Melt chips and Peanut butter<br>Remove from heat and quickly stir in the other ingredients.  Tends to get thick quickly.<br>Pat out on cookie sheet<br>Freeze 20 minutes and cut into 1 inch squares<br>Tips for cooking with carob chips:  If the chips don''t melt well, add a small amount of water 2 T. at a time until chips melt but are still thick looking.  The fudge won''t set if you add too much water.', 108, NULL, NULL);
INSERT INTO recipes (16, '3 Bean Salad II', '', '4 cups green beans<br>2 cups red bell pepper (chopped)<br>1 cup garbanzo beans<br>1 cup kidney beans<br>2 tbsp red onion (minced)<br>2 tbsp vinaigrette dressing<br>2 tbsp fresh lemon juice', 'Clean and steam green beans for 4-5 minutes until tender.<br>Combine all ingredients and toss.  Chill before serving.', 110, NULL, NULL);
INSERT INTO recipes (17, 'Garden Pasta Salad', '', '1lb cooked pasta<br>1/4 red onion (diced)<br>1 carrot (sliced and quarterd)<br>1 small can black olives (sliced)<br>1/4 cup peas (corn optional)<br>1 clove garlic (miced)<br>1/2 cup balsamic vinegar<br>salt and pepper to taste', 'Mix all the ingredients together.  Add more vinegar to taste.  Toss and chill before serving.', 110, NULL, NULL);
INSERT INTO recipes (18, 'Chewy Peanut Butter  Cookies', '122 calories and 15 grams carbohydrates.', '1-1/2 cups oats (finely ground in food processor or oat flour)<br>1 cup unbleached all-purpose flour<br>1/2 tsp baking powder<br>1/2 tsp baking soda<br>3/4 tsp sea salt<br>1 cup unsalted peanut butter<br>2 tbsp water<br>1/2 cup maple syrup<br>1 tsp vanilla extract<br>1 cup Sucanat<br>2/3 cup soy margarine', 'Preheat oven to 350 degrees.  In a small bowl, stir together ground oats, flour,baking powder, baking soda, and salt. Set aside.<br>Use beater to mix peanut butter and<br>water together in large bowl until smooth. Add maple syrup and vanilla and beat until well blended.  Add Sucanat and margarine and beat until incorporated.<br>Add dry ingredients and beat on low speed just until completely blended.<br><br>Drop dough by tablespoonfuls onto baking sheets lined with parchment. Press a criss-cross pattern into tops of cookies with the tines of a dinner fork.<br>Bake until tops are lightly browned but cookies are still slightly soft, 10-12 min. Cool on racks.  Cookies can be stored in an air-tight container for several days.', 104, NULL, NULL);
INSERT INTO recipes (19, 'Raspberry Sauce', '', '2 pints red raspberries<br>1 cup cherry juice<br>2 tbsp maple syrup', 'Blen all ingredients.  Chill before serving.', 114, NULL, NULL);
INSERT INTO recipes (20, 'Olive Hummus', NULL, '2 cups black olives<br>12 oz can garbanzo beans<br>Juice of 1 lemon<br>1 large clove of garlic<br>1/3 cup tahini (ground sesame seed paste)<br>2 tbsp olive oil<br>some fresh dill or green onions (optional)', 'Blend all ingredients in a food processor or blender. Add water to thin to desired texture.', 114, NULL, NULL);
INSERT INTO recipes (21, 'Quick Yummy Squash', 'You can make this vegan by adding soy margarine and cinnamon instead of butter and honey for the topping.', '1 whole sqash', 'Take whole squash and poke several holes in it with a fork. Put it in microwave and cook until it partially caves in.  Time varies according to size of squash and microwaves - times range from 9 to 18 minutes.<br>Optional Sauce ideas - Melt butter or margarine.  Add honey and <br>cinnamon.  Scoop seeds out of<br>squash and pour this tasty<br>topping on.  YUMMY!', 103, NULL, NULL);
INSERT INTO recipes (22, 'Ginger and Chives "Sour Cream" Dip', NULL, '1 package soft silken tofu (organic)<br>3 tbsp canola oil<br>3 tbsp lemon juice<br>1 - 2 tbsp brown rice vinegar<br>1 - 2 tbsp ginger juice<br>1/2 tsp salt (optional)<br>3 tbsp chopped chives', 'Blend all ingredients until smooth.  Chill before serving.', 114, NULL, NULL);
INSERT INTO recipes (23, 'Tabooli', '', '1 cup bulgar wheat<br>1 cup cold water<br>1/2 cup minced parsley<br>1/2 cup green onion<br>1/4 cup fresh mint leaves<br>1/4 cup green pepper<br>1 large tomato (chopped)<br>1/4 cup olive oil<br>1/4 cup lemon juice', 'Rinse bulgar several times. Combine with cold water and let stand one hour.<br>Drain excess liquid. Mix all ingredients.  Refrigerate at least one hour.', 110, NULL, NULL);
INSERT INTO recipes (24, 'Korean Fried Rice', 'Family recipe - old world Korean style fried rice.', '2 tbsp oil for frying tofu<br>1 lb firm tofu<br>3 tbsp soy sauce<br>1/3 cup oil for frying veggies and rice<br>1 medium onion (chopped)<br>1 medium carrot (chopped)<br>1 cup frozen peas<br>4 cups cold cooked rice<br>salt to taste', 'Heat 2 tbsp oil in a frying pan. Crumble the tofu with a fork. Fry in the pan for about 9 minutes til the water has evaporated. Drizzle the soy sauce over the tofu, mix to spread evenly then transfer to a plate to cool off.<br>Heat the 1/3 cup oil in the frying<br>pan and fry the onion and carrots.<br>After about 3 minutes, add the peas. Cook until the carrots are tender and the peas are warm.<br>Add the rice and mix in the vegetables.  Fry for 3-5 minutes.<br>Add the crumbled tofu.  Salt the<br>fried rice to taste.', 116, NULL, NULL);
INSERT INTO recipes (25, 'Grumbles', 'The following recipe is very<br>versitile.  It could be used in chili, sloppy joes, tacos, tortillas, enchiladas or spaghetti.  At the July potluck it was used in the Zesty Garden Casserole.', '4 1/2 cups water<br>1 cup walnuts<br>2 cups bulgar wheat<br>1/2 cup dried onion flakes<br>1 1/2 tsp. onion powder<br>1/4 tsp. garlic powder', 'Blend 2 cups water and walnut in<br>blender until not nut pieces<br>remain.<br>Then put remaining water along with bulgar and seasonings in a pan and simmer covered for 20 min, stirring occasionally.<br>Bake for 1 1/2 hours at 350 degrees.  Turn every 15 min.  (Like fried hamburger - not to crunchy).<br>To reconstitue add 1 cup salsa or diced canned tomatoes.', 103, NULL, NULL);
INSERT INTO recipes (26, 'Brown Gravy', 'Delicious Brown Gravy from our 1st Annual Dinner and Buffet.', '2 ? tbsp Soy Margarine<br>1/4 cup  Flour  ?Wondra?<br>1 ? cups Vegetable Broth (?Imagine? no-chicken flavor)<br>2 tbsp Soy Sauce ?Tamari?<br>?  tsp  Black Pepper<br>1/8 cup Dried Parsley', 'Melt margarine in a small sauce pan over medium heat.  Add flour and whisk together about 30 seconds.<br>Then add broth slowly while whisking. Cook over medium heat for 2 minutes.<br>Add soy sauce and pepper. Continue cooking until gravy is thickened.', 113, '2001-12-10 22:32:19', NULL);
INSERT INTO recipes (27, 'Stuffing', 'Stuffing from 1st Annual Dinner and Buffet.', '9x13 Cake Pan<br>2 cups Diced Onions<br>2 cups Diced Celery<br>2 tbsp Sesame Oil<br>2 bags ?Pepperidge Farm Stuffing - herb seasoned cubes"<br>2 tsp Dried Parsley<br>2 tsp Poultry Seasoning<br>4 cups Vegetable Broth ( ?Imagine? no-chicken flavor)', 'Saute onion and celery in oil.<br>Mix cubes, seasoning, sauted vegetables, and broth in large bowl until cubes are well coated.<br>Place mixture in cake pan and bake at 350 until lightly browned.', 113, '2001-12-10 23:01:01', NULL);
INSERT INTO recipes (28, 'Broccoli Salad', 'Awsome Broccoli Salad from the 1st Annual Dinner and Buffet.', '1 bunch Broccoli<br>1 small Red Onion<br>1 cup Sunflower Seeds<br>1 cup Raisins<br>Dressing:<br>2 tbsp Sugar<br>2 tbsp Vinegar<br>1cup Mayonaise ("Vegenaise")', 'Cut Broccoli and Onion.  Mix in Sunflower seeds and Raisins.<br>Mix dressing ingredients together and add to salad right before serving.', 110, '2001-12-10 23:09:04', NULL);
INSERT INTO recipes (29, 'Apple Crunch', 'Apple Crunch from 1st Annual Dinner and Buffet.', '6 apples - peeled and sliced<br>? cup oatmeal (uncooked)<br>? cup brown sugar<br>? cup  flour<br>? cup margarine', 'Place sliced apples in a 8x8 pan.<br>Mix margarine, sugar, flour, oatmeal until crumbly. Place on top of apples.<br>Bake at 350 for 35 to 40 minutes.', 108, '2001-12-10 23:15:31', NULL);
INSERT INTO recipes (30, 'Pumpkin Spice Cake', 'Pumpkin Spice cake from the 1st Annual Dinner and Buffet.', '1 2/3 cup flour<br>1 cup packed brown sugar<br>1 tsp allspice<br>1 tsp baking soda<br>? tsp salt<br>? cup water<br>? cup vegetable oil<br>? tsp vinegar<br>? cup canned pumpkin<br>FROSTING:  ? cup margarine<br>8oz. cream cheese ("tofutti")<br>2 cups powder sugar<br>1 tsp vanilla<br>pinch of salt', 'Pre-heat oven 350.<br>Mix the flour, brown sugar, allspice, baking soda, and salt in an oiled pan (8x8x2inch).<br>Stir in the remaining ingredients, and bake for 35 to 40 minutes. Allow to cool.  Frost and serve when cooled.<br>FROSTING: Cream the ingredients.  Beat until smooth.', 106, '2001-12-10 23:20:52', NULL);
INSERT INTO recipes (31, 'Baked Beans', '', '1/2 c. diced onion<br>1/2c. diced green pepper<br>1/2 c. cubed firm tofu<br>1/2 c. diced apples<br>3-15oz can Great NorthernBeans drained<br>1/2 c. ketchup<br>1 tbsp dry mustard<br>1/4 c. blackstap molasses<br>1 tbsp apple cider vinegar<br>salt and pepper to taste', 'Preheat oven to 350 degrees.<br>Saute onions, green pepper and tofu in water until onion is soft. Combine all ingedients in an oven proof casserole.<br>Cover and bake 30 minutes; then uncover and bake 30 minutes.', 113, '2001-12-20 00:46:57', NULL);
INSERT INTO recipes (32, 'Pumpkin Chip Cookies', '', '1 c. shortening<br>2 c.sugar<br>2 c. cooked pumkin<br>2 tsp vanilla<br>4 c. flour<br>2 tsp baking soda<br>2 tsp cinnamon<br>12oz chocolate chips<br>nuts optional', 'Cream shortening with sugar. Add vanilla, pumpkin, and stir. Mix the rest of the ingredients together. Bake at 325 until golden brown 5-10 minutes', 104, '2001-12-20 01:19:13', NULL);
INSERT INTO recipes (33, 'Brown Rice with Chili and Green Chilies', '', '1 c. brown rice<br>15 oz can Hormel Vegetarian Chili w/Beans<br>4 oz green chiles', 'Cook one cup of brown rice. Add 15oz. can of Hormel Vegetarian Chili w/Beans. Add 4oz can of green chiles.Mix and heat at 350 degree oven for 30 minutes.<br>You may serve with tortilla chips or use as filling for burritos that you cover with a can of enchilada sauce.', 116, '2001-12-20 01:38:26', NULL);
INSERT INTO recipes (34, 'Broccoli Vegetable Quiche', '', '1 prepared pie shell (vegan)<br>1 T. olive oil<br>1 medium onion, diced<br>1 green bell pepper, chopped<br>1 cup sliced fresh mushrooms<br>1 lb firm tofu, patted dry with a paper towel<br>pinch of grated nutmeg<br>1/2 t. turmeric<br>1/2 bunch fresh basil, chopped (or 1 T. dried basil)<br>1/2 t. salt<br>pepper to taste<br>1/2 cup soy milk', 'Preheat oven to 425 degrees F.<br><br>Bake pie crust 8 to 12 minutes.<br>Heat the olive oil in a medium saucepan over medium heat, and saute the onion, green pepper, broccoli, and mushrooms until cooked, about 8 to 10 minutes.<br>In a blender or food processor, blend the tofu, nutmeg, turmeric, basil, salt, and soy milk until smooth.  Stir the vegetables and the tofu mixture together and add pepper to taste.<br>Pour the batter into the pie crust. Bake for 30 minutes, or until a knife inserted just off-center into the quiche comes out clean.', 111, '2001-12-20 01:56:09', NULL);
INSERT INTO recipes (35, 'Delicious Apple Crisp', '', '6-8 peeled and sliced apples<br>1 c. brown sugar<br>3/4 c. flour<br>1/3 c. olive oil<br>1 tsp cinnamon', 'Put sliced apples in pan. Mix oil, sugar, flour. Pour over the top of the sliced apples. Bake at 350 for 35-40 minutes.', 108, '2001-12-20 02:00:16', NULL);
INSERT INTO recipes (36, 'No-Bake Coconut-Chocolate Drops', '', '1/2c. margarine<br>2c. sifted powdered sugar<br>3c. shreaded coconut<br>2oz. unsweetened chocolate', 'Melt margarine. Remove from heat. Add powdered sugar to margarine. Add coconut, mix well. Shape into balls. Make indent in center fill with melted chocolate. Keep chilled.', 104, '2001-12-22 21:36:41', NULL);
INSERT INTO recipes (37, 'Potato Patties Herb Style', '', '8-10 cooked peeled potatoes<br>1 large onion diced<br>2 stalks celery diced<br>2 garlic cloves crushed<br>2 Tbsp. Parsley<br>1/2 tsp. oregano<br>1/2 tsp dill<br>1/4 tsp.sage<br>1/4 tsp. onion salt<br>1/4 tsp. onion salt,  1/4 tsp. onion powder<br>2 Tbsp. Bragg Aminos, 1/2 cup water', 'Put cooked peeled potatoes in a bowl and whip,until creamy. Saute the rest of the ingredient in a pan. Add saute food into the whipped potatoes. Let cool. Shape into patties. Then cover in breading (Falafel Dry Mix). Bake at 400 degrees, for 30 minutes.', 113, '2001-12-22 22:07:54', NULL);
INSERT INTO recipes (38, 'Veggie Prawns (veggie shrimp)', '', 'soybean protein<br>Vegetable oil<br>Natural seasonings', 'Wholesale and retail of Vegetarian Frozen Food Prpducts. Order by telephone 877-668-2668 or web-site: <br>www.vegieworld.com', 112, '2001-12-22 22:44:06', NULL);
INSERT INTO recipes (39, 'Low Fat Enchiladas', 'These are very easy to make enchiladas for a quick meal.', '1 8 oz can of Red or Tomatillo green enchilada sauce<br>6 corn tortillas<br>6 Tbsp of "Tofutti" Garlic Soy Cream Cheese<br>1 medium bunch spinach or 2 medium zucchini<br>3 green onions (diced)<br>1 cup of "Tofutti" Soy Shredded Cheddar Cheese', 'Clean and prepare the spinach or zucchini.  Shred the zucchini or finely chop the spinach.<br>Spray an 8x8 square pan or a casserole dish with cooking spray.  Preheat the oven to 350 degrees.<br>Mix the zucchini or spinach with the Tofutti shredded cheese.<br>Microwave the corn tortillas for 10 seconds for easier folding and pliability.<br>Spread 1 tablespoon of the Tofutti soy cream cheese on each tortilla.<br>Fill each tortilla with the cheese and vegetable mixture.  Do not over fill since it will make it difficult to wrap the tortilla.<br>Fold each tortilla into a cylindrical shape.  Then place into the pan or casserole dish.<br>Once all the enchiladas have been folded and placed into the pan, pour the enchilada sauce over them until they are all covered.  Cover the pan or casserole dish and bake in oven for 30 minutes.<br>Remove from oven and serve.  Sprinkle chopped green onions over each enchilada before serving.', 103, '2001-12-28 10:14:08', NULL);
INSERT INTO recipes (40, 'Carob Fudge', '', '2c. Sunspire carbo chips (unsweetened)<br>1c. peanut butter<br>1c. raisins<br>1c. thinly sliced almonds,or coarsely chopped pecans<br>1c. shredded, unsweetened coconut', 'Melt chips and peanut butter. Remove from heat and quickly stir in remaining ingredients.<br>Pat out on cookies sheet 3/8 to 1/2 inch thick.<br>Freeze 20 minutes, and cut into 1 inch squares. Store in plastic bags in freezer. Serve cold.<br>* I prefer to make balls out of the fudge and then roll in coconut.', 108, '2002-01-17 00:12:44', NULL);
INSERT INTO recipes (41, 'Chocolate Peanut Butter  Chex Mix', '', '8 Tbsp margarine<br>1c. vegan chocolate chips<br>1/2c. peanut butter<br>6c. Rice Chex cereal<br>2c. powdered sugar<br>1/2 c. raisins', 'Put powdered sugar in paper bag.<br>Melt margarine, chips, and peanut butter. Mix in chex mix and raisins.<br>Put chex mix in paper bag. Shake until chex mix is covered with sugar.', 114, '2002-01-17 00:35:33', NULL);
INSERT INTO recipes (42, 'Molassses Creams', '', '1/2 c. shortening<br>1/2 c. sugar<br>1 inch tofu<br>1/2 c. molasses<br>1 c. flour<br>1 1/2 tsp  ground cloves<br>1/2 c. coffee with 1/4 tsp baking soda<br>1 c. raisins<br>1 tsp cinnamon<br>1/2 tsp salt<br>1 1/2 tsp baking soda', 'Cream shortening, molasses, and sugar together mix well.<br>Beat tofu then add to mixture.<br>Add fusion coffee to mixture.<br>Mix dry ingredients together then add to mixture and stir until smooth. Stir in raisins.<br>Pour into 9x13 greased pan. Bake at 350 degrees for 20-25 minutes.<br>Mix one cup powdered sugar with 2 tsp. rice milk to form a thick paste, and frost.', 108, '2002-01-17 00:49:04', NULL);
INSERT INTO recipes (43, 'Veggie Pizza  Appetizer', '', '2 pkg crescent  roll dough<br>2/3c. sour supreme<br>2 8oz. pkg Better Than Cream Cheese, softened<br>1/4c. sliced green onion with tops<br>1 tsp dill weed<br>1 tsp garlic powder<br>1 tsp garlic powder<br>3 c. chopped veggies raw of choice<br>nutritional yeast flakes', 'Unroll crescent roll dough onto 10x15 inch pan. Press scams together. Bake at 350 degrees for 10 minutes, or until golden brown. Cool.<br>Mix together remaining ingredients except yeast flakes and chopped veggies. Spread mixture over the rolls and top with chopped veggie.<br>Sprinkle pizza with nutritional yeast flakes.', 114, '2002-01-19 23:38:34', NULL);
INSERT INTO recipes (44, 'Herbed Deep-Dish Pizza', 'Pizza Hut Pan Crust like pizza.', '1 pkg active yeast<br>1c. luke warm water<br>1 Tbsp sugar<br>2 Tbsp oil<br>2 1/2c. unbleached white flour<br>2 tsp oregano<br>1 1/ 2  tsp garlic<br>1 tsp basil<br>1/2 tsp thyme<br>1 tsp salt<br>2c. pizza sauce<br>fresh mushrooms,onions, olives,jalapenos<br>1 10oz. frozen spinach', 'Combine yeast and water and let dissolve (10 min.). Add sugar and oil to yeast mixture. Combine flour, herbs, and salt.<br>Mix together yeast and flour mixtures and knead for 8 min. until dough loses stickiness(add flour as needed). Let rise in warm place until doubled in size(about 1 1/2 hours)in a flour bowl (covered).<br>Generously oil a 9 inch deep dish pan or 12 spaced muffin pan. Roll out dough 11 inch diameter. Place in the pan and pinch edges for crust. For muffins cut out 2 inch circles and place in muffin tins.<br>Add the pizza sauce and then veggies. Sprinkle a little olive oil on top. Pre-heated oven 425 degrees. Bake for 12-14 minutes. Let cool.', 103, '2002-01-20 10:49:53', NULL);
INSERT INTO recipes (45, 'Chocolate Covered Nuts', '', '1c. semi-sweet chocolate chips<br>1c. nuts of your choice', 'Melt chocolate on low. Constantly stirring. Add nuts and spread on wax paper to a thin layer.<br>Refrigerate until solid. Serve by breaking apart.', 114, '2002-01-20 11:23:01', NULL);
INSERT INTO recipes (46, 'Simple Pizza', '', 'ready- made pizza crust<br>8oz pizza cheese<br>2 oz chopped black olives<br>5 green olives w/pimentos sliced<br>4 oz canned mushrooms pieces drained<br>8 oz can pizza sauce<br>1/2 c. chopped green onions w/top<br>1/2c. chopped green peppers', 'Spread pizza sauce over pizza crust. <br><br><br><br><br><br><br><br>Spread pizza sauce over crust. Sprinkle all other ingredients except cheese. Last ingredient is cheese.<br><br><br><br>Pre-heat oven to 450 degrees and bake 7-12 minutes.', 114, '2002-01-30 19:56:05', NULL);
INSERT INTO recipes (47, 'American Cheese', 'NOTE: Use of gelatin (animal by-product)', '1 cup water<br>1/3 cup and 1 tbsp Emes unflavored gelatin<br>1-1/4 cup boiling water<br>2 cups raw cashews<br>1/4 cup nutritional yeast flakes<br>1 tbsp salt<br>2 tsp onion powder<br>1/4 tsp garlic powder<br>1/4 cup fresh lemon juice<br>4 oz jar pimentos or 1/2 large sweet pepper', 'Soak gelatin in the 1 cup water<br>in blender.<br>Pour boiling water over<br>soaked gelatin and whiz briefly to<br>disssolve.  Cool slightly.<br>Add cashews and liquefy.  Add remaining indregients.  Liquefy until mixture is the consistency of a creamy sauce and no bits of pimento or pepper are visible.<br>Pour into a 1 quart mold and<br>cool slightly.<br>Cover and refrigerate overnight before seving.  After cheese is firm, it may be frozen.', 113, '2002-02-01 11:04:10', NULL);
INSERT INTO recipes (48, 'Melty Cheese', 'Topping for the casserole by Donna Dickie', '4 oz jar pimentos<br>1 tbsp arrowroot or cornstarch<br>1/2 cup cashew nuts<br>1 cup water<br>2 tsp salt<br>1/4 cup nutritional yeast flakes<br>1 1/2 tbsp lemon juice<br>t tsp onion powder', 'Blend all ingredients until smooth.<br>Add to a pot of 1 1/2 cups of boiling water.  Cook until thick.  Serve warm over tacos, haystacks, corn chips, or any of your favorite foods for a zesty vegan cheesy taste.', 113, '2002-02-01 11:49:12', NULL);
INSERT INTO recipes (49, 'Zesty Garden Casserole', '', '4 - 5 Roma tomatoes<br>2 medium zucchini<br>4 - 5 small yellow summer squash<br>1/4  - 1/2 tsp dried or handful fresh basil, chives, oregano, and parsley<br>1/2 medium onion<br>5 cloves garlic (minced)<br>1/4 tsp garlic salt (to taste)<br>grumbles (optional)<br>melty cheese', 'Saute the squash in water adding the salt and herbs, then set aside.<br><br>Saute onion and garlic in olive oil.<br>Layer the squash, tomatoes, *grumbles seasoned with italian seasoning, onion, garlic and melty cheese.<br>Cover and bake at 375 until bubbles through.<br>* Could substitute pasta instead of grumbles or leave out the grumbles<br>altogether.', 105, '2002-02-01 11:56:36', NULL);
INSERT INTO recipes (50, 'Butter Crunchies', '', '1/2 cup peanut butter<br>2 tbsp honey<br>2 cups granola<br>Pinch of salt<br>1 1/2 tsp vanilla', 'Use your hands to mix all ingredients.  Then form into balls.', 104, '2002-02-01 12:09:52', NULL);
INSERT INTO recipes (51, 'Balkan Cucumber Salad', '', '1/2 cup yellow onion<br>4 cucumbers<br>1 tsp salt, mint, pepper, and scallions<br>1/4 cup parsley<br>1 1/2 cup yogurt<br>1 tsp dill<br>1 clove garlic<br>1 cup walnuts', 'Soak onions in cold water for 30 minutes. Cut cucumbers into thin rounds.  Add remaining ingredients and mix (not walnuts).  Sprinkle on walnuts just  before serving.', 110, '2002-02-01 12:13:08', NULL);
INSERT INTO recipes (52, 'Thai Noodle Salad', '', '1 cup picante sauce (Pace mild salsa)<br>1/4 cup peanut butter<br>2 tbsp brown sugar<br>2 tbsp orange juice<br>1 tsp soy sauce<br>1/2 tsp ginger<br>1 box fettucini noodles', 'Add veggies and or beans (ie. kidney beans, broccoli, bell pepper etc..) and mix all together.  Top with fresh cilantro. (peanuts optional)', 110, '2002-02-01 12:18:51', NULL);
INSERT INTO recipes (53, 'Organic Salad', '', '8 ripe tomatoes<br>1 organic red onion<br>7 fresh picked sprigs of parsley<br>10 of the biggest basil leaves you can find on the plant<br>Juice from one lemon<br>1/4 cup vinegar<br>1/4 cup olive oil<br>1 tsp Braggs (soy sauce)<br>1 tsp Sucanat<br>1 bay leaf', 'Cut cup the onion, parsley and basil as fine as you like. Add this to the lemon juice, vinegar, olive oil, soy sauce, bay leaf, and sucanat.<br>Cut one of the tomatoes and add to the salad.  Let sit for a couple of hours so the flavors blend.<br>Take out the bay leaf and add the remaining tomatoes (diced).  Stir and serve chilled.', 110, '2002-02-01 12:26:56', NULL);
INSERT INTO recipes (54, 'Lovely Light Lemon Cake', '', '2 c. unbleached flour<br>1 1/2 tsp. baking powder<br>1 tsp. baking soda<br>1/2 tsp. sea salt<br>1 Tbsp. lemon zest<br>1/3 c. canola oil<br>3/4 c. maple syrup<br>1/3 c. vanilla soymilk or ricemilk<br>1/3 c. water<br>1/4 c. plus 2 Tbsp. lemon juice<br>2 tsp. apple cider vinegar<br>1 tsp. vanilla extract<br>2 tsp. lemon extract', 'Sift together dry ingredients.<br>In another bowl combine wet ingredients.<br>3. Add wet ingredients to the dry and mix quickly and thoroughly until smooth.<br>Pour into cake pan(s). Bake for 25-30 minutes at 350 degrees.', 106, '2002-02-25 21:28:57', NULL);
INSERT INTO recipes (55, 'Dreamy Cream Whip', '', '2 c. silken tofu<br>2/3 c. corn syrup<br>1/2 c. canola oil<br>2 Tbsp. vanilla<br>2 Tbsp. lemon juice<br>pinch of salt', 'Combine in a blender and add water only if needed to keep the mixture moving.  Use like you would whipped cream.', 108, '2002-02-25 21:41:39', NULL);
INSERT INTO recipes (56, 'Vegan Brown Rice', '', '1 Cup uncooked Long grain Brown Rice<br>2 heads of broccoli(medium size flowerettes); 2 red sweet peppers(sliced); 1/3 cup olive oil, 1 Tablespoon Tamari, 1/4 cup raw cider vinegar', 'Steam cook rice<br><br>Steam broccoli and peppers till tender<br>Mix together rice and vegetables and dress with olive oil, Tamari and raw cider vinegar', 116, '2002-02-28 18:20:02', NULL);
INSERT INTO recipes (57, 'Chili and Chips Delight', '', 'Three-15 oz cans Hormel Vegetarian Chili with Beans<br>one-32 oz bag Ore-Ida Cottage Fries<br>10 oz can of Chi-Chis Fiesta diced tomatoes with Green Chilies', 'In a large microwaveable bowl mix the three cans of chili with the can of diced tomatoes, heat in microwave to desired temperature<br>Heat oven to 450 degrees, place the cottage fries on enough flatware to lay them out individually, cook for about 18-20 minutes, turning once if desired<br>Serve the two separate dishes, take potatoes and then pour chili over the potatoes', 113, '2002-02-28 19:14:29', NULL);
INSERT INTO recipes (58, 'Tabuli', '', 'one cup dry bulghar wheat<br>one and a half cups boiling water<br>one and a half teaspoons salt<br>1/4 fresh lemon and/or lime juice<br>one heaping teaspoon crushed, fresh garlic<br>1/2 cup chopped scallions<br>1/2 teaspoon dried mint<br>1/4 cup  olive oil<br>fresh black pepper<br>two medium tomatoes diced<br>1 packed cup freshly chopped parsley<br>1 chopped cucumber', 'Begin to soak bulghar at least 3 hrs before serving time.<br>Combine bulghar, boiling water and salt in a bowl. Cover and let stand 15-20 minutes or until bulghar is chewable.<br>Add lemon juice, oil and mint and mix. Refrigerate 2-3hrs.<br>Just before serving add the vegetables and mix gently.', 110, '2002-02-28 19:31:31', NULL);
INSERT INTO recipes (59, 'Mediterannean Tofu Salad', '', 'SALAD<br>1 lb firm tofu, cubed<br>1 cup garbanzo beans(drained, canned or cooked and dried)<br>1/2 cup black olives, pitted and sliced(preferably Kalamata)<br>1/2 cup chopped parsley<br>1/2 medium red onion, peeled and sliced in thin matchsticks<br>3 ripe, medium tomatoes, diced<br>1 small cucumber, seeded and diced<br>DRESSING<br>1/3 cup olive oil<br>2 tablespoons red wine vinegar<br>2 garlic cloves, minced<br>1 teaspoon Dijon mustard<br>2 teaspoons honey<br>1 tablespoon dried oregano<br> 1 teaspoon cayenne pepper, or to taste<br>1/2 teaspoon salt<br>1/4 teaspoon white pepper', 'Place the tofu, beans, olives, parsley and vegetables in a salad bowl<br>Whisk together all the dressing ingredients and pour salad. Toss lightly and serve.', 110, '2002-02-28 19:54:20', NULL);
INSERT INTO recipes (60, 'Calypso Stew', 'Calypso beans are a black and white bean. You can substitute pinto,kidney or garbanzo beans', '2 c. calypso beans<br>1 tsp. cumin<br>1.5 tsp. McKays Chicken Seasoning<br>1 tsp. Vege-Sal<br>salt to taste<br>1 tsp. basil<br>1/2 tsp. paprika<br>1/4 tsp. oregano<br>1 tsp. dill weed<br>1 large carrot, sliced or diced<br>2 c. shredded green cabbage<br>1 c. shredded red cabbage<br>1 large onion, cut in long, thin strips<br>1 c. sliced celery<br>3 c. canned diced tomatoes in juice', 'Soak dry beans overnight or boil in water and let soak 1 hr. Drain and discard water.<br>Cover soaked beans with water in a large kettle 1 inch above beans(or use 4 c. of canned beans of your choice). Bring to a boil and simmer until nearly done, about 2 hrs.<br>Add seasonings ansd diced carrot; cook for 15 min., then add the remaining ingredients and cook 15-20 min. longer or until vegetables are tender. You may need to add more water to achieve the consaistency of a thick stew. Serve over rice or couscous.', 103, '2002-04-01 16:49:20', NULL);
INSERT INTO recipes (61, 'Calico Beans', '', '12 oz. textured vegetable protein(TVP)<br>1/2 c. BacOs<br>1/2 - 1 c. chopped onion<br>1 can kidney beans( 16 oz.)<br>1 can lima beans(16 oz.)<br>1 - 28 oz vegetarian baked beans<br>3/4 c. brown sugar<br>1/2 c. catsup<br>2 Tbsp. apple cider vinegar', 'Preheat oven to 350 degrees. Saute onion in small amount light vegetable or olive oil until tender. Add TVP to onions and heat through.<br>In 2.5-3 qt. casserole dish combine beans, brown sugar, catsup and vinegar and mix until well blended. Add BacOs and onion/TVP mixture and stir well.<br>Bake uncovered for 45 to 60 min.', 113, '2002-04-01 17:10:50', NULL);
INSERT INTO recipes (62, 'Angel Hair Pasta with Basil Pesto', 'Adapted from Vegetarian Nights: fresh from Hawaii written by Bonnie Mandoe.', '1/2 c. extra virgin olive oil<br>2 cloves garlic<br>4 c. fresh basil leaves, packed<br>1/2 c. pine nuts<br>salt to taste<br>1 lb Angel Hair Pasta', 'Place pesto ingredients in blender.<br>Blend to a smooth paste.<br>Cook pasta according to pkg. directions.<br>After draining pasta, mix the pesto in immediately.', 101, '2002-04-01 17:34:31', NULL);
INSERT INTO recipes (63, 'Curried Potatoes and Peas', '', '3 Tbsp. vegetable oil<br>1 onion, finely chopped<br>1 inch fresh ginger, peeled and finely chopped<br>2 garlic cloves, chopped<br>1 tsp. turmeric<br>16 oz. frozen peas<br>8 oz. potatoes, cubed<br>1 tsp. salt<br>2 Tbsp. coriander leaves, chopped<br>juice of 1/2 lemon', 'Heat  oil in a saucepan. Add the onion and fry until it is soft. Stir in ginger, garlic and cook for an additional 5 min., stirring frequently. Stir in turmeric, then peas, potatoes and salt.<br>Reduce the heat to low, cover the pan and simmer for 20 min. Add a spoonful or two of water if the mixture becomes too dry.<br>Stir in coriander leaves and lemon juice, and simmer for another 10 to 15 min., or until the vegetables are tender.', 115, '2002-04-01 18:24:15', NULL);
INSERT INTO recipes (64, 'Yummy Dal', '', '7 c. vegetable stock or vegetable bouillon<br>1 lb yellow or green split peas<br>1 medium onion, chopped<br>2 medium carrots, sliced into 1/4 inch rounds<br>2 medium boiling potatoes, unpeeled, cut into 1/2 inch cubes<br> juice of 1 lemon<br>2 Tbsp. canola oil<br>2 tsp. cumin seeds<br> 1 tsp. black mustard seeds<br>2 tsp. curry powder<br>1 tsp. ground coriander<br>1 tsp. ground cumin<br>1 tsp. turmeric<br>1 tsp. finely grated fresh ginger<br>2 garlic cloves, minced<br>1 tsp fine sea salt<br>1/8 tsp. cayenne pepper', 'Put the vegetable stock and split peas into a large pot. Cover and bring to a boil over medium-high heat, then immediately reduce the heat and simmer until the peas are beginning to get tender, about 25 min.<br>Stir in the onion, carrots and potatoes, cover, and return to a boil over medium-high heat. Immediately reduce the heat and simmer, stirring occasionally, until the potatoes are almost tender, about 15 min.<br>Meanwhile, in a small frying pan, heat the canola oil over medium heat.<br>Add the cumin seeds, mustard seeds, curry powder, coriander, cumin, turmeric, ginger, garlic, salt and cayenne.<br>Cook, stirring constantly, until the mustard seeds begin to pop, about 30 sec. Remove from the heat.<br>Stir  the spice mixture and lemon juice into  the split peas.<br>Cover and return to a boil over medium-high heat, then immediately reduce the heat and simmer, stirring occasionally, for 5 min.<br>Mash some of the vegetables and split peas until the DAL reaches the desired consistency. Serve immediately.', 115, '2002-04-01 18:44:17', NULL);
INSERT INTO recipes (65, 'Easy Lemon Sugar Snaps', '', '3/4 c. Earth Balance, softened<br>3 Tbsp. applesauce<br>1 pkg. 2-layer-size vegan lemon cake mix<br>1 c. yellow cornmeal<br>2 Tbsp. finely shredded lemon peel, coarse sugar or granulated sugar', 'Preheat oven to 375 deg.<br>In a large mixing bowl beat butter and egg with an electric mixer on medium to high speed for 30 sec. Gradually beat in cake mix until combined; stir in cornmeal and lemon peel.<br>Using 1 Tbsp of dough for each cookie, roll  into 1-in.balls. Roll in sugar. Place 2 in. apart on ungreased cookie sheets. Bake for 9 or 10 min. or until bottoms are lightly browned. Let cool on cookie sheet for 1 min. Transfer to wire rack. Cool complete', 104, '2002-04-27 20:21:48', NULL);
INSERT INTO recipes (66, 'Banana Blueberry Ice Cream', '', '1/4 c. ground flaxseed<br>2 bananas<br>1-16oz. bag frozen fruit', 'Place in blender with 1/2 c. of water. Eat or freeze for later.', 108, '2002-04-27 20:33:31', NULL);
INSERT INTO recipes (67, 'Mock Vegan Chicken Salad', '', '"Wheat Meat"<br>"Vegenaise"<br>pasta noodles<br>olives<br>celery<br>onion powder', 'Mix in ingredients in a bowl and serve.', 110, '2002-04-27 20:36:38', NULL);
INSERT INTO recipes (68, 'Tofu Corn Bread Casserole', '', '2 cans organic corn<br>One 1 lb. tofu (crumbled)<br>1 box organic cornmeal muffin mix<br>4 oz.(8Tbsp) vegan sour supreme(Tofutti)<br>2 Tbsp soy margarine (chopped up or melted)<br>Add garlic, black pepper and sea salt to taste', 'Mix all ingredients together in a bowl.<br>Grease a casserole dish.<br>Bake at 350 deg. for 1 hr. or until knife in center comes out clean.', 105, '2002-04-27 20:40:13', NULL);
INSERT INTO recipes (69, 'Cauliflower and Onion Pakoras', 'Traditional Indian Appetizer', '2 med. onions, cut into slices<br>1 head of cauliflower, cut into 1 in. flowerettes<br>2 c. Besan(gram) flour<br>3 tsp. chili pepper<br>4 tsp. coriander/cumin powder (50/50 mix)<br>2.5 tsp. salt<br>4 tsp. sugar<br>Juice of 1 lemon', 'Mix all ingredients (except for veggies) together to form a smooth batter.<br>Preheat deep fryer at 340 deg.<br>Dip each  cut veggie  piece and place in fryer( batch size depends on fryer capacity).<br>Fry for 10 min. for cauliflower and 5 min. for onion.', 114, '2002-04-27 20:51:04', NULL);
INSERT INTO recipes (70, 'Delicious Vegan Dip', '', 'Tofutti " cream cheeze "<br>Hormel Vegetarian Chili<br>Tofutti " sour cream "', 'Spread "cream cheese" on the bottom of a pie pan, pour can of chili on top.  Bake at 350 for 30 min. Eat with tortilla chips.', 110, '2002-04-27 21:00:52', NULL);
INSERT INTO recipes (71, 'Yummy Vegetarian Salad', '', 'Celery<br>frozen or fresh peas<br>fresh tomatoes<br>cucumbers<br>onion<br>garlic- minced<br>carrots<br>broccoli<br>cauliflower<br>mushrooms<br>twisted macaroni (colored)<br>dill weed<br>black pepper<br>garlic powder<br>parmesan cheese as desired<br>Italian Dressing', 'Mix ingredients and serve.', 110, '2002-04-27 21:04:58', NULL);
INSERT INTO recipes (72, 'Calico Beans', '', '12 oz. textured vegetable protein(TVP) o.k. to use 6 oz. TVP<br>1/2 c. "BacOs"<br>1/2 to 1 c. chopped onion<br>One 16oz. can kidney beans<br>One 16oz. Lima beans<br>One 28oz. can  vegetarian baked beans<br>3/4 c. brown sugar<br>1/2 c. catsup<br>2 Tbsp. apple cider  vinegar', 'Preheat oven to 350 deg.<br>Saute onion in small amount light vegetable or olive oil until tender. Add TVP to onion and heat through.<br>In 2.5 to 3 qt. casserole dish combine beans, brown sugar, catsup and vinegar. Mix until well blended. Add "BacOs" and onion/TVP mixture and stir well.<br>Bake uncovered for 45 to 60 min.', 113, '2002-04-27 21:20:30', NULL);
INSERT INTO recipes (73, 'Glazed Carrots', 'From PETA''s "The Compassionate Cook"', '2 c. peeled and sliced carrots<br>1 medium onion, diced<br>1/4 c. packed light brown sugar<br>2 Tbsp margarine, melted<br>1/4 tsp. ground cinnamon', 'Steam carrots in small amount of water until tender, about 8 min. Drain thoroughly.<br>In a medium saucepan, combine carrots, onion, brown sugar, margarine and cinnamon. Mix well. Simmer for 15 minutes or until onions are tender. Stir occasionally.', 115, '2002-04-27 22:14:49', NULL);
INSERT INTO recipes (74, 'Vegetable/Grape Salad', '', 'Avacodo<br>Tomatoes<br>Yellow pepper<br>onions<br>garlic<br>olives<br>grapes<br>lettuce<br>Dressing<br>vinegar<br>balsamico<br>lemons<br>olive oil<br>honey', 'Mix ingredients. Toss and serve.', 110, '2002-04-28 21:23:14', NULL);
INSERT INTO recipes (75, 'Andy''s Vegan Pizza', '', 'SROUT GRAIN CRUST<br>2 c. wheat berries,  soak for 2 days<br>1/4 c. olives<br>2 cloves garlic<br>MOCK CHEEZE<br>1 pkg. Mori Nu firm tofu<br>1 pkg. Mori Nu Italian mix', 'Oil pizza pan.<br>Masticate in a food mill wheat berries, olives and garlic.<br>Blend together components of Mock Cheeze(Mori Nu firm Tofu and Mori Nu Italian mix)<br>Add your choice of toppings and bake at 400 deg. for 20 min.', 103, '2002-04-28 21:29:09', NULL);
INSERT INTO recipes (76, 'Lentil Pate', '', '1 c. lentils, uncooked (7.5 oz.)<br>4 c. water<br>2 tsp. vegetable oil<br>1 to 1.5 c. chopped onions<br>1/4 tsp. salt<br>1/4 tsp. pepper', 'Bring water to a boil in a medium saucepan. Add lentils, cover pot, reduce heat, and simmer 40 to 45 min. until lentils are tender. Drain.<br>Heat oil in a large nonstick skillet over medium heat. Add onions and cook until browned. Stir frequently and add small amounts of water, if necessary to avoid sticking.(The secret to the flavor of this dish is to make sure the onions are nicely browned.)<br>In a food processor, combine lentils, cooked onions, salt, and peppers. Process until smooth.<br>Transfer to a bowl and chill several hours to blend flavors.<br>Serve on crackers or make a delicious sandwich on rye bread with lettuce, tomato and sliced onion.', 114, '2002-04-28 21:40:49', NULL);
INSERT INTO recipes (77, 'Cheese Factory Corn Bread', '', '1 c. flour<br>1 c. yellow cornmeal<br>1 Tbsp. baking powder<br>1 tsp. salt<br>1/2 c. sugar<br>3 large eggs, beaten or egg replacer<br>1/2 c. buttermilk<br>1/2 c. milk<br>1/4 c. butter, melted<br>1 tsp vanilla extract', 'Preheat oven to 425 deg.<br>Spray a 9-in. round cake pan with nonstick cooking spray and lightly flour.<br>Mix flour, cornmeal, baking powder, salt and sugar in a medium bowl. In a separate bowl, combine eggs, buttermilk, milk, butter and vanilla; stir well.<br>Add egg mixture to dry ingredients; stir just until ingredients are moistened.( Do not overmix.)<br>Pour batter into prepared pan and bake 20 to 25 min. until golden in color.<br>Remove from oven and invert onto round plate. Carefully remove from plate and cool on wire rack.', 109, '2002-04-28 22:17:57', NULL);
INSERT INTO recipes (78, 'QUICK SPANISH RICE', '', '1 medium onion thinly sliced<br>1 medium green pepper chopped<br>28 oz tomato sauce<br>1 tsp prepared mustard salt pepper<br>1/4 c. oil', 'Add onion, green pepper, rice and 1/4 c. oil in pan. Stir on high heat until lightly brown.<br>Add tomato paste, 2-1/2 c. hot water and remaining ingredients. Bring to a boil then cover and simmer 25 min.', 116, '2002-06-09 10:49:01', NULL);
INSERT INTO recipes (79, 'Green Beans and Chickpeas', '', 'Dressing----- 2 T. red wine vinegar, 1 T. olive oil, 1/2 tsp. garlic, minced<br>2 C. green beans cut into 1 in. pieces<br>1 C. chickpeas, rinsed and drained', 'Mix together the dressing ingredients and set aside.<br>Steam the green beans for 4 min.<br>Toss together the steamed green beans and the chickpeas with the dressing. Serve warm or chilled.', 110, '2002-06-09 10:56:16', NULL);
INSERT INTO recipes (80, 'Original Pepper Tofu Jerky', '', '1 lb. extra-firm tofu<br>1/2 c. Braggs or soy sauce<br>3-4 Tbsp liquid smoke<br>1/8 c. water<br>1 Tbsp. onion powder<br>1 tsp garlic powder or 1 clove garlic, crushed<br>1 Tbsp. pepper<br>1 tsp. sweetner', 'Cut the drained tofu into long narrow strips(about 1/4 in. thickness).<br>In a small bowl, whisk together the Braggs, liquid smoke, water, onion powder, garlic, pepper and sweetner.<br>Place the tofu strips in a shallow baking pan or on a cookie sheet and pour the marinade over them. Let them marinate for several hours or overnight for best results.<br>Cook the tofu in a food dehydrator or bake in the oven for about 4-6 hours at 200 deg. F.<br>Turn the tofu over once every hour so it bakes evenly. Continue until the texture is very chewy, but not crisp.<br>Tofu jerky will keep indefinitely. Store in a container with a tight-fitting lid.', 114, '2002-06-09 11:02:57', NULL);
INSERT INTO recipes (81, 'Spanish Dal', '', '1 Tbsp. oil<br>1 onion, peeled and chopped<br>1 garlic glove, crushed<br>1 dried red chili, crumbled<br>1 tsp. ground coriander<br>1/4  tsp. ground tumeric<br>2/3 c. lentils<br>2-1/2 c. water<br>1/2 lb spinach washed<br>salt and pepper', 'Heat oil in a large saucepan and put in onion. Let them cook covered for 10 min.<br>Add garlic, chili and spices. Cook for 1 min., stirring, then add lentils. Stir, then add water. Bring to boil. Simmer, uncovered for 30 min.until soft.<br>Add spinach to pan and cook for about 10 min.<br>Add season and serve.', 113, '2002-06-09 11:36:46', NULL);
INSERT INTO recipes (82, 'Jeera Rice', '', '1 c. rice<br>2 c. water<br>1 black cardemom<br>2 cinnamon sticks<br>1 bay leaf<br>2 tsp. cumin seeds<br>2 Tbsp. Ghee<br>Salt to taste', 'Wash and soak rice 1/2 hour before preparing. Drain the rice.<br>Heat ghee and add cumin seed, bay leaf, cardemom and cinnamon sticks.<br>When cumin seeds sizzle, add drained rice and stir to coat rice.<br>Add salt and water. Bring to a boil. Reduce the heat to low and cover with a lid. Leave a little opening for the steam to escape.<br>When holes appear on top, rice is done.', 116, '2002-06-09 11:47:18', NULL);
INSERT INTO recipes (83, 'Sesame Pasta Salad', '', '4 oz. dry pasta<br>1 carrot, shredded<br>1/2 bell pepper, slivered<br>2 radishes, sliced<br>1 green onion, minced<br>Dressing:<br>3 Tbsp. Rice Vinegar<br>1 Tbsp. Soy Sauce<br>2 Tbsp. Oil<br>2 Tbsp. Sesame seeds<br>Pepper to taste', 'Cook pasta.  Drain.<br>Mix together the dressing ingredients.<br>Combine pasta, vegetables, and dressing.  Toss gently.', 110, '2002-06-24 21:10:16', NULL);
INSERT INTO recipes (84, 'Cucumber Pea Salad', '', '8 oz. pasta rings, cooked al dente<br>1 c. fresh or frozen peas, steamed<br>1/2 c. chopped onion<br>1 large cucumber, chopped<br>1/4 c. chopped chives<br>1 c. Vegenaise<br>Salt, pepper, and some sugar to taste (can use sucanat)', 'Combine pasta and veggies. Separately mix vegenaise and spices. Add everything together.  Serve chilled.', 110, '2002-06-24 21:27:06', NULL);